import * as React from 'react';
import Icon from 'react-native-vector-icons/FontAwesome';
import vh from 'react-native-css-vh-vw';
import { StyleSheet, Text, View, Button } from 'react-native';
import ProductSection from './ProductSection';
import generalStyleSheet from "../general/GeneralStyleSheet";


export default class ProfileTab extends React.Component {

    constructor() {
        super();
    }

    products=null;
    userData=null;

    render() {
        return (
            <View style={generalStyleSheet.viewContainer}>
                <Text style={generalStyleSheet.bigTitle}>Profilo</Text>
                <View style={[style.profileContainer,generalStyleSheet.bigBorder]}>
                    <View style={style.leftColumn}>
                        <View style={style.firstProfileRow}>
                            <Icon name="user-circle" size={100}/>
                            <Text style={[style.usernameText,generalStyleSheet.mediumTitle]}>Username</Text>
                        </View>
                        <View style={style.profileRow}>
                            <Text style={[generalStyleSheet.normalText,style.profileText]}>Nome: Alessandro</Text>
                            <Text style={[generalStyleSheet.normalText,style.profileText]}>Cognome: Cavaglià</Text>
                            <Text style={[generalStyleSheet.normalText,style.profileText]}>DataNascita: 29/06/1999</Text>
                            <Text style={[generalStyleSheet.normalText,style.profileText]}>Email: cavagliascuola@gmail.com</Text>
                        </View>
                    </View>
                    <View style={style.rightColumn}>
                        <Text style={[generalStyleSheet.bigTitle,style.productTitle]}>I tuoi prodotti</Text>
                        <View style={style.productsView}>
                            <ProductSection products={this.products}/>
                        </View>
                    </View>
                </View>
                {this.renderBlockingView()}
            </View>
        );
    }
    renderBlockingView(){
        if(this.userData==null){
            return <View style={[style.notLoggedTab,generalStyleSheet.bigBorder]}>
                <View>
                    <Text style={generalStyleSheet.bigTitle}>Sembrerebbe che tu non sia loggato</Text>
                    <Button title="Login">Login</Button>
                </View>
            </View>
        }
    }

}
const style=StyleSheet.create({
    profileContainer:{
        display:"flex",
        flexDirection:"row",
        width:"100%",
        marginTop:"3%",
        height:vh(70),
    },
    notLoggedTab:{
        width:"100%",
        marginTop:"3%",
        height:vh(80),
        position:"absolute",
        backgroundColor:"white",
        display:"flex",
        alignItems:"center",
        justifyContent:"center",
    },
    leftColumn:{
        display:"flex",
        flexDirection:"column",
        width:"25%",
        borderRightWidth:1
    },
    rightColumn:{
        display:"flex",
        flexDirection:"column",
        width:"75%"
    },
    firstProfileRow:{
        display:"flex",
        flexDirection:"row",
        width:"100%",
        paddingLeft:"10%",
        paddingTop:"10%",
        borderBottomWidth:1,
    },
    profileRow:{
        display:"flex",
        flexDirection:"column",
        width:"100%",
    },
    usernameText:{
        paddingLeft:"12%",
        height:"100%",
        lineHeight:100,
        paddingTop:"4%",
        textAlign:"center"
    },
    profileText:{
        paddingLeft:"10%",
        paddingBottom:"2%",
        borderBottomWidth:1,
    },
    productTitle:{
        textAlign:"center",
        marginTop:"6%",
        flex:0.1
    },
    productsView:{
        display:"flex",
        flex:0.9,
        width:"100%",
        marginTop:"2%"
    },
})
