import * as React from 'react';
import { StyleSheet, Text, View,ActivityIndicator } from 'react-native';
import generalStyleSheet from "../general/GeneralStyleSheet";



/* Propos:
    products : the list of products to show
 */

const styles=StyleSheet.create({
    container:{
        flex:1,
        margin:"5%"
    },
    centeredContainer:{
        display:"flex",
        alignItems:"center",
        justifyContent:"center",
        flex:1
    },
    indicator:{
        marginTop:"15%"
    }
})

export default class TinderCard extends React.Component {
    constructor() {
        super()
    }




    render(){
        if(this.props.products==null)
            return <View style={styles.container}>
                <View style={styles.centeredContainer}>
                    <View>
                        <Text style={generalStyleSheet.bigTitle}>Caricamento in corso</Text>
                        <ActivityIndicator style={styles.indicator} size="large" color="#000"/>
                    </View>
                </View>
            </View>
        else
            return <View>

            </View>

    }
}
