import { StyleSheet} from 'react-native';

export default StyleSheet.create({
    viewContainer:{
        marginTop:"15%",
        marginLeft:"5%",
        marginRight:"5%"
    },
    bigTitle:{
        fontSize:30,
        fontWeight:"bold",
    },
    mediumTitle:{
        fontSize:25,
        fontWeight:"bold"
    },
    normalText:{
        fontSize:20,
    },
    bigBorder:{
        borderWidth:3,
        borderColor:"black",
        borderRadius:25,
    },
    visible:{
        opacity:100
    },
    invisible:{
        opacity:0
    }
})
